
var DependencyQueryBuilder  = org.jboss.forge.addon.dependencies.builder.DependencyQueryBuilder;
var DependencyRepository    = org.jboss.forge.addon.dependencies.DependencyRepository

exports.resolve = function( qry ) {

        var dq = DependencyQueryBuilder.create( qry );

        var cd = self.dependencyResolver.resolveVersions(dq);

        return cd.toArray();
}


exports.resolveFromRepo = function( qry, repo ) {

        var dq = DependencyQueryBuilder.create( qry )
                //.setScopeType("")
                //.setFilter( function(dep) { return true; } )
                  .setRepositories( new DependencyRepository( repo.id, repo.url ) )       
        ;

        var cd = self.dependencyResolver.resolveVersions(dq);

        return cd.toArray();
}
